    private void jButtonDividirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonDividirActionPerformed
        // TODO add your handling code here:
		int numerador = Integer.parseInt(jTextFieldNumerador.getText());
		int denominador = Integer.parseInt(jTextFieldDenominador.getText());
		int divisao = numerador / denominador;
		int resto = numerador % denominador;
		jLabelDivisao.setText(Integer.toString(divisao));
		jLabelResto.setText(Integer.toString(resto));
    }//GEN-LAST:event_jButtonDividirActionPerformed

